const writeFile = require('./lib/writeFile');
const { palettes, webcolors } = require('@johnshopkins/brand-colors');

const lines = [
  '// This file is generated by `npm run create-colors-scss'
];

// SCSS Variables
lines.push('', '// SCSS Variables', '// Brand Colors');

// Compile web colors as SCSS variables
webcolors.all.map(color => {
  lines.push(`$jhu-brand-${color.slug}: #${color.hex};`)
});

// Compile expanded palette colors as CSS variables
lines.push('', '// Expanded Palettes');
for (const name in palettes) {
  const paletteName = name.replace(' ', '-').toLowerCase();
  for (const grade in palettes[name]) {
    lines.push(`$jhu-${paletteName}-${grade}: #${palettes[name][grade].hex};`);
  }
}

// lines.push('', '// Aliases', '$black: $double-black;');
lines.push('', '// Alerts',
  '$jhu-alert-high: $jhu-brand-dark-red;',
  '$jhu-alert-elevated: $jhu-brand-gold;',
  '$jhu-alert-low: $jhu-brand-homewood-green;',
  '$jhu-alert-info: $jhu-brand-medium-blue;',
);






// CSS Variables
lines.push('', '', '// CSS Variables', ':root {', '  // Brand Colors');

// Compile web colors as CSS variables
webcolors.all.map(color => {
  lines.push(`  --jhu-brand-${color.slug}: #{$jhu-brand-${color.slug}};`)
});

// Compile expanded palette colors as CSS variables
lines.push('', '  // Expanded Palettes');
for (const name in palettes) {
  const paletteName = name.replace(' ', '-').toLowerCase();
  for (const grade in palettes[name]) {
    lines.push(`  --jhu-${paletteName}-${grade}: #{$jhu-${paletteName}-${grade}};`);
  }
}

// lines.push('', '// Aliases', '$black: $double-black;');
lines.push('', '  // Alerts',
  '  --jhu-alert-high: #{$jhu-alert-high};',
  '  --jhu-alert-elevated: #{$jhu-alert-elevated};',
  '  --jhu-alert-low: #{$jhu-alert-low};',
  '  --jhu-alert-info: #{$jhu-alert-info};',
);

lines.push('}');


// write
writeFile('./src/shared/scss/variables/colors.scss', lines);
